import React, { useState } from 'react'
import QuestionBank from './ArrayData'

const App = () => {
let [QuestionCount,setQuestionCount]=useState(0);
let [tryPageHide,setTryPageHide]=useState(false);
let [scoreCount,setScoreCount]=useState(0)

const PitchureChange=(CorAnswer)=>{
if (CorAnswer==true){
  setScoreCount(scoreCount+1)
}
 const NextData = QuestionCount+1
if (NextData<QuestionBank.length) {
  console.log(NextData);
  setQuestionCount(NextData) 

}else{
  setTryPageHide(true)
}}
return (
 <div> 


{ tryPageHide? (
 (<div className="TryagainBox">
 <h1>**!!GAME ENDED!!**</h1>
 <h2 className='Chances'>Chances over you did very Well</h2> <br />
 <h4 className='playaGain'><strong>You got {scoreCount} score out of {QuestionBank.length-1} </strong> </h4>
 <button className='tryAgainBtn btn btn-danger'>Play again </button> 
 </div>) )

             :( <> <div className='backImage'>
              <h1><span>Question <h1></h1>{QuestionCount} /</span> {QuestionBank.length-1} </h1>
              <img className='ImgStyle' src={QuestionBank[QuestionCount].AnimalPic} alt="Animal_image" />
              <h4>Choose the correct answer </h4>
              <div className='btn-fild'>
              {QuestionBank[QuestionCount].buttonOptions.map((Btnoptions,index)=>{
              return <button onClick={()=> PitchureChange(Btnoptions.CorAnswer)} key={index} className="btn btn-dark"> {Btnoptions.option} </button>})}
              </div>
              </div>
              </>)
}
 </div> 

)}

export default App;
