import React, { useState } from 'react'
import QuestionBank from './ArrayData'

const App = () => {
  console.log(QuestionBank.length);
let [QuestionCount,setQuestionCount]=useState(0);
let [tryPageHide,setTryPageHide]=useState(false)


const PitchureChange=()=>{
 const NextData =QuestionCount+1
if (QuestionCount<QuestionBank.length) {
  setQuestionCount(NextData) 
}}



return (
<div>
{<>tryPageHide?
(<div className="TryagainBox">
<h1>**!!GAME ENDED!!**</h1>
<h2 className='Chances'><strong>5 chances over </strong> you did very Well</h2> <br />
<h4 className='playaGain'>You want to play again </h4>
<button className='tryAgainBtn btn btn-danger'>Play again </button> 
</div>)

            : (<>
              <div className='backImage'>
              <h1><span>Question {QuestionCount} /</span> {QuestionBank.length-1} </h1>
              <img className='ImgStyle' src={QuestionBank[QuestionCount].AnimalPic} alt="Animal_image" />
              <h4>Choose the correct answer </h4>
              <div className='btn-fild'>
              {QuestionBank[QuestionCount].buttonOptions.map((Btnoptions,index)=>{
              return <button onClick={PitchureChange}  className="btn btn-dark"> {Btnoptions.option} </button>})}
              </div>
              </div>
              </>
</>}
</div>

)}

export default App;
